var health = 50;

// + - * / % **

health = health + 10; // 60
health = health % 50; // 10
// Check if it's even: health % 2 == 0
health = health ** 2; // 100

// +=, -=,  *=, /=

health -= 20; // health = health - 20

// ++, -- 
health ++; // 81
health --; // 80

// health = (health * 2) + (health / 5)

//Homework
health = health + 1;
health ++;
health += 1;